#pkg load optim;#format long;##data=csvread("lambda.csv");#n=524288;#t=data([1:n],1);#lambda=data([1:n],2);args = [   5.022440501216771e+06   4.890899807169174e+00   5.022680079071247e+06   6.244922881883948e+00   1.668285465684735e-02];#   -1.887243500212101e-14dargs = [  1e-3  1e-3  1e-3  1e-3  1e-3];L = @(t,p) polyval([1/p(1),p(2)],t);M = @(t,p) polyval([1/p(3),p(4)],t);#E = @(t,p) polyval([p(5),p(6)],t);E = @(t,p) p(5);C = @(t,p) 1.25*sin(2*M(t,p)).*E(t,p).^2 + 2*E(t,p).*sin(M(t,p));LAM = @(t,p) L(t,p) + C(t,p);func = @(t,p) sin(lambda-LAM(t,p));[mylambda,newargs,cvg,iter]=leasqr(t,zeros(size(t)),args,func,0.000001,20,ones(size(t)),dargs);cvgiternewargs-argsrad2deg(asin(max(abs(func(t,newargs))))) # 1.157736806426570e-02 1.089176053792885e-02newargs